import React, { useEffect, useState } from "react";
import './styles.css'; // Import your CSS file

function App() {
    const [tickets, setTickets] = useState([]);
    const [groupBy, setGroupBy] = useState('status');
    const [sortBy, setSortBy] = useState('priority');

    useEffect(() => {
        // Fetch tickets from the API
        const fetchTickets = async () => {
            const response = await fetch('https://api.quicksell.co/v1/internal/frontend-assignment');
            const data = await response.json();
            setTickets(data.tickets); // Assuming the API response has a 'tickets' property
        };
        fetchTickets();
    }, []);

    const groupedTickets = tickets.reduce((acc, ticket) => {
        const key = ticket[groupBy] || 'No Group'; // Default to 'No Group' if group value is missing
        if (!acc[key]) acc[key] = [];
        acc[key].push(ticket);
        return acc;
    }, {});

    const sortedTickets = Object.values(groupedTickets).flat().sort((a, b) => {
        if (sortBy === 'priority') {
            return b.priority - a.priority; // Descending order for priority
        }
        return a.title.localeCompare(b.title); // Ascending order for title
    });

    return (
        <div className="App">
            <div className="header">
                <div className="controls">
                    <select onChange={(e) => setGroupBy(e.target.value)} value={groupBy}>
                        <option value="status">Group by Status</option>
                        <option value="user">Group by User</option>
                        <option value="priority">Group by Priority</option>
                    </select>
                    <select onChange={(e) => setSortBy(e.target.value)} value={sortBy}>
                        <option value="priority">Sort by Priority</option>
                        <option value="title">Sort by Title</option>
                    </select>
                </div>
            </div>
            <div className="kanban">
                {sortedTickets.map(ticket => (
                    <div className={`ticket-card status-${ticket.priority.toLowerCase()}`} key={ticket.id}>
                        <div className="ticket-title">{ticket.title}</div>
                        <div className="ticket-priority">Priority: {ticket.priority}</div>
                        <div className="ticket-description">{ticket.description}</div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
