import React, { useEffect, useState } from 'react';
import { fetchTickets } from '../api';
import Card from './Card';

const KanbanBoard = () => {
  const [tickets, setTickets] = useState([]);
  const [groupedTickets, setGroupedTickets] = useState({});
  const [grouping, setGrouping] = useState('status');
  const [sort, setSort] = useState('priority');

  useEffect(() => {
    const fetchData = async () => {
      const ticketData = await fetchTickets();
      setTickets(ticketData);
    };
    fetchData();
  }, []);

  useEffect(() => {
    let sortedTickets = [...tickets];

    if (sort === 'priority') {
      sortedTickets.sort((a, b) => b.priority - a.priority);
    } else if (sort === 'title') {
      sortedTickets.sort((a, b) => a.title.localeCompare(b.title));
    }

    const groupBy = sortedTickets.reduce((acc, ticket) => {
      const key = ticket[grouping];
      if (!acc[key]) acc[key] = [];
      acc[key].push(ticket);
      return acc;
    }, {});

    setGroupedTickets(groupBy);
  }, [tickets, grouping, sort]);

  const handleGroupingChange = (value) => setGrouping(value);
  const handleSortChange = (value) => setSort(value);

  return (
    <div>
      {Object.keys(groupedTickets).map((groupKey) => (
        <div key={groupKey} className="kanban-column">
          <h3>{groupKey}</h3>
          {groupedTickets[groupKey].map((ticket) => (
            <Card key={ticket.id} ticket={ticket} />
          ))}
        </div>
      ))}
    </div>
  );
};

export default KanbanBoard;
